<?/*Шаблон индекса админки======================*/?><!DOCTYPE html><html xmlns="http://www.w3.org/1999/xhtml"><?# Подключение шапки админкиinclude_once dirname(__FILE__) . '/../design/adminHeader.tpl.php';?>	<br/>    <div class="container-fluid">		<ul class="breadcrumb">			<li>				<a title="Вернуться к началу" href="index.php"><i class="icon-home"></i>&nbsp;Админ.интерфейс</a>				<span class="divider">/</span>				</li>			<li>				<a href="authors.php">Авторы</a>				<span class="divider">/</span>			</li>			<li class="active">				<a href="authors.php?author=<?= $author['id_author'] ?>">Автор #<?= $author['id_author'] ?></a>			</li>		</ul>			<? if (isset($message)): ?>		<div class="alert alert-error fade in">			<button type="button" class="close" data-dismiss="alert">&times;</button>			<?= $message ?>		</div>		<? endif ?>				<form action="" class="form-horizontal" method="post">			<? if (isset($message)): ?>			<p style="color: red;"><?= $message ?></p>			<? endif; ?>			<div class="control-group">				<label class="control-label" for="inputEmail">Email:</label>				<div class="controls">					<input type="text" name="author_email" id="inputEmail" placeholder="Email" value="<?= $author['email'] ?>">				</div>			</div>			<div class="control-group">				<label class="control-label" for="inputName">Имя:</label>				<div class="controls">					<input type="text" name="author_name" id="inputName" placeholder="Имя" value="<?= $author['name'] ?>">				</div>			</div>			<div class="control-group">				<label class="control-label" for="inputPayment">Реквизиты:</label>				<div class="controls">					<input type="text" name="author_payment" id="inputPayment" placeholder="Платёжные реквизиты" value="<?= $author['payment'] ?>">				</div>			</div>					<? if (count($products) > 0): ?>			<h5 class="products_list btn">Список товаров (<?= count($products) ?>)</h5>			<table id="products_list" class="table table-striped table-bordered table-hover table-condensed">			<tr>				<th>ID</th>				<th>Название</th>				<th>Стоимость</th>			</tr>			<? foreach ($products as $product): ?>			<tr>				<td><a href="<?= $config['sitelink'] ?>jsale/admin/codegen.php?id_product=<?= $product['id_product'] ?>"><?= $product['id_product'] ?></a></td>				<td><?= $product['title'] ?></td>				<td><?= $product['price'] ?> <?= $config['currency'] ?></td>			</tr>			<? endforeach; ?>			</table>			<hr/>			<? endif ?>						<? if (count($all_orders) > 0): ?>			<div class="well">			<h4>Заказы</h4>			<p>Заказов оформлено: <?= $total_count ?></p>			<p>Заказов оплачено: <?= $paid_count ?></p>			<hr/>			<p>Заработано: <?= number_format($paid_sum, 2, '.', '') ?> <?= $config['currency'] ?></p>			</div>			<h5 class="orders_list btn">Список заказов (<?= count($all_orders) ?>)</h5>			<table id="orders_list" class="table table-striped table-bordered table-hover table-condensed">			<tr>				<th>ID</th>				<th>Дата</th>				<th>Статус</th>				<th>Товары</th>				<th>Сумма</th>				<th>Комиссия</th>			</tr>			<? foreach ($all_orders as $order): ?>			<tr>				<td><a href="<?= $config['sitelink'] ?>jsale/admin/orders.php?order=<?= $order['id_custom'] ?>"><?= $order['id_custom'] ?></a></td>				<td><?= $order['date'] ?></td>				<td <?= (in_array($order['status'], $success_statuses)) ? 'class="text-success"' : '' ?>><?= $config['statuses'][$order['status']] ?></td>				<td><? foreach ($order['items'] as $item): ?><?= $item['product'] ?> - <?= $item['quantity'] ?> <?= $item['unit'] ?> по <?= ($config['discounts']['fixed'] === true) ? number_format($item['price'] - $item['discount'], 2, '.', '') : number_format($item['price'] * (1 - $item['discount'] / 100), 2, '.', '') ?> <?= $config['currency'] ?><br><? endforeach; ?></td>				<td><?= $order['sum'] ?> <?= $config['currency'] ?></td>				<td><?= (isset($order['commission'])) ? $order['commission'] : 0 ?> <?= $config['currency'] ?></td>			</tr>			<? endforeach; ?>			</table>						<hr />			<? endif ?>						<? if ($id_author != 'new'): ?>			<h5>Всего заработано: <?= number_format( $paid_sum, 2, '.', '') ?> <?= $config['currency'] ?></h5>			<h5>Всего выплачено: <?= $author['paid'] ?> <?= $config['currency'] ?></h5>			<? $for_payment = number_format($paid_sum - $author['paid'], 2, '.', '');?>			<h4 <? if ($for_payment > 0): ?>class="text-success"<? endif ?>>Осталось выплатить: <?= $for_payment ?> <?= $config['currency'] ?></h4>			<? endif; ?>						<div class="form-actions">				<input type="hidden" name="author_id" value="<?= $author['id_author'] ?>">				<? if ($id_author != 'new' && $for_payment > $author['paid']): ?>					<input type="submit" name="author_pay" class="btn btn-primary" value="Выплатить" onclick="return confirm('Выплата уже сделана?')">					<input type="hidden" name="for_payment" value="<?= $for_payment ?>">					<input type="hidden" name="paid" value="<?= $author['paid'] ?>">				<? endif; ?>				<input type="submit" name="author_edit" class="btn btn-success" value="Сохранить">				<? if ($id_author != 'new'): ?>				<input type="submit" name="author_delete" class="btn btn-danger" value="Удалить" onclick="return confirm('Вы действительно хотите удалить партнёра?')">				<? endif; ?>				<input type="submit" name="author_cancel" class="btn" value="Отмена">			</div>		</form>    </div>	<script type="text/javascript">	$(document).ready(function() {			$(".orders_list").click(function () {			$("#orders_list").toggle('fast');		});		$(".products_list").click(function () {			$("#products_list").toggle('fast');		});	});</script>	</body></html>