<?/*Шаблон индекса админки======================*/?><!DOCTYPE html><html xmlns="http://www.w3.org/1999/xhtml"><?# Подключение шапки админкиinclude_once dirname(__FILE__) . '/../design/adminHeader.tpl.php';?>	<br/>    <div class="container-fluid">		<ul class="breadcrumb">			<li>				<a title="Вернуться к началу" href="index.php"><i class="icon-home"></i>&nbsp;Админ.интерфейс</a>				<span class="divider">/</span>				</li>			<li class="active">				<a href="authors.php">Авторы</a>			</li>		</ul>			<? if (isset($message)): ?>		<div class="alert alert-error fade in">			<button type="button" class="close" data-dismiss="alert">&times;</button>			<?= $message ?>		</div>		<? endif ?>				<table class="table table-striped table-bordered table-hover table-condensed">		<thead>			<tr>				<th>Email</th>				<th>Всего заработано, <?= $config['currency'] ?></th>				<th>Выплачено, <?= $config['currency'] ?></th>				<th>К выплате, <?= $config['currency'] ?></th>				<th>Реквизиты для выплат</th>				<th></th>			</tr>		</thead>		<tbody>			<? $total_paid = $total_profit = 0; ?>			<? foreach ($authors as $author): ?>			<? $author_profit = $author['paid_sum'];			if ($author_profit > $author['paid']): ?>			<tr>				<td><a href="?author=<?= $author['id_author'] ?>"><?= $author['email'] ?></a></td>				<td><?= $author_profit = number_format($author_profit, 2, '.', '') ?></td>				<td><?= $author['paid'] ?></td>				<td><?= $for_payment = $author_profit - $author['paid'] ?></td>				<td><?= $author['payment'] ?></td>				<td>					<form method="post" action="">						<input type="submit" name="author_payment" class="btn btn-success" value="Выплатить" onclick="return confirm('Выплата уже сделана?')">						<input type="hidden" name="for_payment" value="<?= $for_payment ?>">						<input type="hidden" name="paid" value="<?= $author['paid'] ?>">						<input type="hidden" name="author_id" value="<?= $author['id_author'] ?>">					</form>				</td>			</tr>			<? $total_paid += $author['paid']; $total_profit += $author_profit;  ?>			<? endif; ?>			<? endforeach; ?>		</tbody>		</table>				<p>Всего заработано: <?= number_format($total_profit, 2, '.', '') ?> <?= $config['currency'] ?></p>		<p>Всего выплачено: <?= number_format($total_paid, 2, '.', '') ?> <?= $config['currency'] ?></p>		<h4 <? if ($total_profit > $total_paid): ?>class="text-error"<? endif ?>>Осталось выплатить: <?= number_format($total_profit - $total_paid, 2, '.', '') ?> <?= $config['currency'] ?></h4>    </div>		<?= (isset($pagination)) ? $pagination : '' ?>	</body></html>