<?/*Шаблон формы редактирования заказа в админке============================================*/?><?# Подключение шапки админкиinclude_once dirname(__FILE__) . '/../design/adminHeader.tpl.php';$modals = '';?><br><div class="container-fluid">	<ul class="breadcrumb">		<li>			<a title="Вернуться к началу" href="index.php"><i class="icon-home"></i>&nbsp;Админ.интерфейс</a>			<span class="divider">/</span>			</li>		<li>			<a href="calls.php">Звонки</a>			<span class="divider">/</span>		</li>		<li class="active">			<a href="calls.php?call=<?= $call['id_call'] ?>">Звонок #<?= $call['id_call'] ?></a>		</li>	</ul>    <form action="" class="form-horizontal" method="post">        <? if (isset($message)): ?>        <p style="color: red;"><?= $message ?></p>        <? endif; ?>	<div class="row">	<div class="span6">	<h3>Информация о клиенте</h3><br />				<!--<div class="control-group">			<label class="control-label" for="call_lastname">Фамилия</label>			<div class="controls">				<input type="text" value="<?= $call['lastname'] ?>" name="call_lastname" class="input-large">			</div>		</div>-->		<div class="control-group">			<label class="control-label" for="call_name">Имя</label>			<div class="controls">				<input type="text" value="<?= $call['name'] ?>" name="call_name" class="input-large">			</div>		</div>		<!--<div class="control-group">			<label class="control-label" for="call_fathername">Отчество</label>			<div class="controls">				<input type="text" value="<?= $call['fathername'] ?>" name="call_fathername" class="input-large">			</div>		</div>-->		<div class="control-group">			<label class="control-label" for="call_phone">Телефон</label>			<div class="controls">			<div class="input-append">				<input type="text" value="<?= $call['phone'] ?>" name="call_phone" class="input-large"><span class="add-on"><a href="skype:<?= $call['phone'] ?>?call"<i class="icon-bell"></i></a></span>			</div>			</div>		</div>		<div class="control-group">			<label class="control-label" for="call_fathername">Email</label>			<div class="controls">			<div class="input-append">				<input type="text" value="<?= $call['email'] ?>" name="call_email" class="input-large">				<span class="add-on"><a href="mailto:<?= $call['email'] ?>"<i class="icon-envelope"></i></a></span>			</div>			</div>		</div>		<div class="control-group">			<label class="control-label" for="call_topic">Тема звонка</label>			<div class="controls">			<div class="input-prepend">            <span class="add-on"><i class="icon-check"></i></span><select name="call_topic">            <? foreach ($config['call']['topics'] as $call_topic): ?>				<? if ($call_topic == $call['topic']): ?>				<option value="<?= $call_topic ?>" selected="selected"><?= $call_topic ?></option>				<? else: ?>				<option value="<?= $call_topic ?>"><?= $call_topic ?></option>				<? endif ?>            <? endforeach ?>            </select>			</div>			</div>		</div>	</div>	<div class="span6">	<h3>Информация о звонке</h3><br />		<div class="control-group">			<label class="control-label" for="page-parent">Дата звонка</label>			<div class="controls">			<div class="input-prepend">            <span class="add-on"><i class="icon-calendar"></i></span><input type="text" name="call_date" class="input-medium" value="<?= (isset($call['date'])) ? date('d.m.Y H:i:s', strtotime($call['date'])) : date('d.m.Y H:i:s') ?>">			</div>			</div>		</div>		<div class="control-group">			<label class="control-label" for="call_comment">Комментарий</label>			<div class="controls">				<textarea id="textarea" name="call_comment" class="input-xlarge" rows="3"><?= $call['comment'] ?></textarea>			</div>		</div>		<div class="control-group">			<label class="control-label" for="call_link">Ссылка</label>			<div class="controls">				<input type="text" value="<?= $call['link'] ?>" name="call_link" class="input-large"></span>			</div>		</div>		<div class="control-group">			<label class="control-label" for="call_status">Статус звонка</label>			<div class="controls">			<div class="input-prepend">            <span class="add-on"><i class="icon-check"></i></span><select name="call_status">            <? foreach ($statuses as $id => $status): ?>			<? if (!is_array($status)): ?>				<? if ($id == $call['status']): ?>				<option value="<?= $id ?>" selected="selected"><?= $status ?></option>				<? else: ?>				<option value="<?= $id ?>"><?= $status ?></option>				<? endif ?>			<? endif ?>            <? endforeach ?>            </select>			</div>			</div>		</div>				<? if ($_SESSION['access_type'] == 'admin'): ?>		<div class="control-group">			<label class="control-label" for="call_operator">Оператор</label>			<div class="controls">			<div class="input-prepend">            <span class="add-on"><i class="icon-check"></i></span><select name="call_operator">			<option value="0">-</option>            <? foreach ($operators as $id => $operator): ?>			<? if (is_array($operator)): ?>				<? if ($operator['id_manager'] == $call['operator']): ?>				<option value="<?= $operator['id_manager'] ?>" selected="selected"><?= $operator['name'] ?></option>				<? else: ?>				<option value="<?= $operator['id_manager'] ?>"><?= $operator['name'] ?></option>				<? endif ?>			<? endif ?>            <? endforeach ?>            </select>			</div>			</div>		</div>		<? else: ?>			<? if (!empty($call['operator'])): ?>				<input type="hidden" name="call_operator" value="<?= $call['operator'] ?>">			<? else: ?>				<? foreach ($operators as $operator): ?>				<? if ($operator['id_manager'] == $_SESSION['id_manager']): ?>				<input type="hidden" name="call_operator" value="<?= $operator['id_manager'] ?>">				<? endif ?>				<? endforeach ?>			<? endif ?>		<? endif ?>	</div>	</div>			<? if ($_SESSION['access_type'] == 'admin' && isset($call_statuses)): ?>		<h3>Логи статусов</h3>		<table class="table table-striped table-bordered table-condensed" style="max-width: 300px;">		<thead>			<th>Дата</th>			<th>Статус</th>		</thead>		<tbody>			<? foreach ($call_statuses as $status): ?>			<tr>				<td><?= date('d.m.Y H:i:s', strtotime($status['date'])) ?></td>				<td><?= $config['call_statuses'][$status['status']] ?></td>			</tr>			<? endforeach; ?>		</tbody>		</table>		<? endif; ?>		<div class="form-actions">			<input type="hidden" name="call_last_status" value="<?= $call['status'] ?>">            <input type="hidden" name="call_id" value="<?= $call['id_call'] ?>">			<input type="submit" name="call_save" class="btn" value="Применить">            <input type="submit" name="call_edit" class="btn btn-success" value="Сохранить">            <input type="submit" name="<?= ($call['status'] == $config['call_statuses']['deleted'][0]) ? 'real_call_delete' : 'call_delete' ?>" class="btn btn-danger" value="Удалить" onclick="return confirm('Вы действительно хотите удалить звонок?')">            <input type="submit" name="call_cancel" class="btn" value="Отмена">			<? if ($_GET['call'] != 'new'): ?>			<input type="submit" name="call_create_order" class="btn btn-primary" value="Создать заказ" onclick="return confirm('Вы действительно хотите создать заказ?')">			<? endif ?>        </div>    </form></div><?= $modals ?></body></html>