height() == 0)			$('#search-form').collapse('show');		else			$('#search-form').collapse('hide');		return false;	});		if ($('#search-form').attr('class') != 'no-collapse')	{		$('#search-form').addClass('collapse');		$('#search-form').height(0);	}		$(".ruler1").on('click', function() {		if ($('#prod-search').height() == 0)			$('#prod-search').collapse('show');		else			$('#prod-search').collapse('hide');		return false;	});		if ($("#prod-search").attr('class') != 'no-collapse')	{		$("#prod-search").addClass('collapse');		$("#prod-search").height(0);	}	$('#order_list').change(function(){		$(this).parent().parent().parent().submit();	});		$('.datepicker').datepicker({		'language' : 'ru',		'autoclose' : true,		'weekStart' : 1,		'format' : 'yyyy-mm-dd'	});		$('.tag').change(function(){		$(this).parents('form').submit();	});</script><?= $modals ?><? if (isset($_SESSION['print'])): ?>	<? foreach ($_SESSION['print'] as $id => $print): ?>		<script type="text/javascript">		window.open('../print/print.php?id=<?= $id ?>');		</script>	<? endforeach; ?><? endif; ?></body></html>