<?/*Шаблон пагинации================$navi - массив данных	page - текущая страница	total - общее количество страниц$file - файл, к которому подключается пагинация$config - массив со всеми настройками*/?><? if (isset($navi['total']) && $navi['total'] > 1): ?><div class="pagination pagination-centered">	<ul>	<? if ($navi['page'] != 1): ?>	<li><a href="<?= $config['sitelink'] . $config['dir'] ?>admin/<?= $file ?>.php?page=1<?if(isset($current_category['code'])):?>&category=<?= $current_category['code'] ?><?endif?><?if(isset($status)):?>&status=<?= $status ?><?endif?>" title="Перейти к первой странице">←</a></li>	<? endif ?>	<? if ($navi['page'] - 3 > 0): ?>	<li><a href="<?= $config['sitelink'] . $config['dir'] ?>admin/<?= $file ?>.php?page=<?= $navi['page'] - 3 ?><?if(isset($current_category['code'])):?>&category=<?= $current_category['code'] ?><?endif?><?if(isset($status)):?>&status=<?= $status ?><?endif?>" title="Перейти к <?= $navi['page'] - 3 ?> странице"><?= $navi['page'] - 3 ?></a></li>	<? endif ?>	<? if ($navi['page'] - 2 > 0): ?>	<li><a href="<?= $config['sitelink'] . $config['dir'] ?>admin/<?= $file ?>.php?page=<?= $navi['page'] - 2 ?><?if(isset($current_category['code'])):?>&category=<?= $current_category['code'] ?><?endif?><?if(isset($status)):?>&status=<?= $status ?><?endif?>" title="Перейти к <?= $navi['page'] - 2 ?> странице"><?= $navi['page'] - 2 ?></a></li>	<? endif ?>	<? if ($navi['page'] - 1 > 0): ?>	<li><a href="<?= $config['sitelink'] . $config['dir'] ?>admin/<?= $file ?>.php?page=<?= $navi['page'] - 1 ?><?if(isset($current_category['code'])):?>&category=<?= $current_category['code'] ?><?endif?><?if(isset($status)):?>&status=<?= $status ?><?endif?>" title="Перейти к <?= $navi['page'] - 1 ?> странице"><?= $navi['page'] - 1 ?></a></li>	<? endif ?>	<li class="active"><a title="Вы здесь"><strong><?= $navi['page'] ?></strong></a></li>	<? if ($navi['page'] + 1 <= $navi['total']): ?>	<li><a href="<?= $config['sitelink'] . $config['dir'] ?>admin/<?= $file ?>.php?page=<?= $navi['page'] + 1 ?><?if(isset($current_category['code'])):?>&category=<?= $current_category['code'] ?><?endif?><?if(isset($status)):?>&status=<?= $status ?><?endif?>" title="Перейти к <?= $navi['page'] + 1 ?> странице"><?= $navi['page'] + 1 ?></a></li>	<? endif ?>	<? if ($navi['page'] + 2 <= $navi['total']): ?>	<li><a href="<?= $config['sitelink'] . $config['dir'] ?>admin/<?= $file ?>.php?page=<?= $navi['page'] + 2 ?><?if(isset($current_category['code'])):?>&category=<?= $current_category['code'] ?><?endif?><?if(isset($status)):?>&status=<?= $status ?><?endif?>" title="Перейти к <?= $navi['page'] + 2 ?> странице"><?= $navi['page'] + 2 ?></a></li>	<? endif ?>		<? if ($navi['page'] + 3 <= $navi['total']): ?>	<li><a href="<?= $config['sitelink'] . $config['dir'] ?>admin/<?= $file ?>.php?page=<?= $navi['page'] + 3 ?><?if(isset($current_category['code'])):?>&category=<?= $current_category['code'] ?><?endif?><?if(isset($status)):?>&status=<?= $status ?><?endif?>" title="Перейти к <?= $navi['page'] + 3 ?> странице"><?= $navi['page'] + 3 ?></a></li>	<? endif ?>	<? if ($navi['page'] != $navi['total']): ?>	<li><a href="<?= $config['sitelink'] . $config['dir'] ?>admin/<?= $file ?>.php?page=<?= $navi['total'] ?><?if(isset($current_category['code'])):?>&category=<?= $current_category['code'] ?><?endif?><?if(isset($status)):?>&status=<?= $status ?><?endif?>" title="Перейти к последней странице">→</a></li>	<? endif ?>	</ul></div><? endif ?>