f; ?>								<td><?= $partner_profit = number_format($partner['paid_sum'] + $partner['referers_sum'], 2, '.', '') ?></td>				<td <? if ($partner_profit > $partner['paid']): ?>class="text-error"<? endif; ?>><?= $partner['paid'] ?></td>			</tr>			<? $total_paid += $partner['paid']; $total_profit += $partner_profit;  ?>			<? endforeach; ?>		</tbody>		</table>				<p>Всего заработано: <?= number_format($total_profit, 2, '.', '') ?> <?= $config['currency'] ?></p>		<p>Всего выплачено: <?= number_format($total_paid, 2, '.', '') ?> <?= $config['currency'] ?></p>		<h4 <? if ($total_profit > $total_paid): ?>class="text-error"<? endif ?>>Осталось выплатить: <?= number_format($total_profit - $total_paid, 2, '.', '') ?> <?= $config['currency'] ?></h4>		<a href="?pay" class="btn">Выплаты</a>		<hr/>		<? $invite = ($config['partner']['invites'] === true) ? '?code=' . $config['partner']['admin_invite'] : '' ?>		<p>Ссылка для регистрации партнёра: <a href="<?= $config['sitelink'] . $config['dir'] ?>partner/new.php<?= $invite ?>"><?= $config['sitelink'] . $config['dir'] ?>partner/new.php<?= $invite ?></a></p>    </div>		<?= (isset($pagination)) ? $pagination : '' ?>	</body></html>