<?/*Шаблон списка товаров в админке===============================*/?><?# Подключение шапки админкиinclude_once dirname(__FILE__) . '/../design/adminHeader.tpl.php';?><br><div class="container-fluid">	<ul class="breadcrumb">		<li>			<a title="Вернуться к началу" href="index.php"><i class="icon-home"></i>&nbsp;Админ.интерфейс</a>			<span class="divider">/</span>			</li>		<li class="active">			<a href="products.php">Товары</a>		</li>	</ul>    <p class="text-info">        <span class="label label-important">Важно!</span> При удалении категорий удаляются также и вложенные подкатегории и товары. При переименовании – всё остаётся в БД.<br>        Не рекоммендуется использовать категории более 2 уровня вложенности. То есть, не сложнее схемы: категория → подкатегория → товар.    </p>    <form action="" method="post" class="form-horizontal" enctype="multipart/form-data">        <? if (!empty($_GET['category'])): ?>            <a href="<?= $config['sitelink'] . $config['dir'] ?>admin/products.php?category=<?= $current_category['parent'] ?>">Перейти к родительской категории→</a>        <? endif; ?>        <? if (!empty($categories)): ?>        <h3>Разделы:</h3>        <table class="table table-bordered table-hover table-striped table-condensed">            <tr>                <th>Код</th>                <th>Родитель</th>                <th>Наименование</th>                <th>Действия</th>            </tr>            <? foreach ($categories as $key => $category): ?>            <tr>                <td>                    <input type="text" name="category_code[<?= $key ?>]" value="<?= $category['code'] ?>" size="20">                    <input type="hidden" name="category_id[<?= $key ?>]"  value="<?= $category['id_category'] ?>">                </td>                <td>                    <select name="category_parent[<?= $key ?>]">                        <option value=""></option>                        <? foreach ($all_categories as $cats): ?>                            <? if ($cats['code'] == $category['parent']): ?>                            <option value="<?= $cats['code'] ?>" selected="selected"><?= $cats['title'] ?><? if ($cats['parent']): ?> (<?= $cats['parent'] ?>)<? endif; ?></option>                            <? elseif ($cats['code'] != $category['code']): ?>                            <option value="<?= $cats['code'] ?>"><?= $cats['title'] ?><? if ($cats['parent']): ?> (<?= $cats['parent'] ?>)<? endif; ?></option>                            <? endif; ?>                        <? endforeach; ?>                    </select>                </td>                <td><input type="text" name="category_title[<?= $key ?>]" value="<?= $category['title'] ?>" size="40"></td>                <td>					<a class="btn btn-success" title="Перейти к разделу" href="<?= $config['sitelink'] . $config['dir'] ?>admin/products.php?category=<?= $category['code'] ?>"><i class="icon-eye-open icon-white"></i></a>					<a class="btn btn-danger" href="<?= $config['sitelink'] . $config['dir'] ?>admin/products.php?delete_category_id=<?= $category['id_category'] ?>" title="Удалить категорию"  onclick="return confirm('Вы действительно хотите удалить категорию? При этом будут удалены и все вложенные подкатегории и товары!')"><i class="icon-trash icon-white"></i></a>				</td>	            </tr>            <? endforeach; ?>        </table>        <? endif; ?>        <? if (!empty($products)): ?>        <h3>Товары:</h3>        <table class="table table-bordered table-hover table-striped table-condensed">            <tr>                <th>Название, код</th>                <th>Раздел</th>                <th>Описание</th>				<? if ($config['store']['enabled'] === true): ?>					<th>Остатки</th>				<? endif; ?>                <? if ($config['download']['enabled'] === true): ?>					<? if ($config['download']['link2files'] === true): ?>					<th>Выберите файл</th>					<? else: ?>                    <th>Текущая версия файла. Загрузка нового файла</th>					<? endif; ?>                <? endif ?>				<? if ($config['manager']['enabled'] === true): ?>					<th>Менеджер</th>				<? endif ?>				<? if ($config['partner']['enabled'] === true && $config['partner']['rate_product']  === true): ?>                    <th>Комиссия партнёра</th>                <? endif ?>				<? if ($config['author']['enabled'] === true): ?>                    <th>Автор</th>                <? endif ?>				<? if ($config['reports']['cost_prices'] === true): ?>					<th>Себестоимость</th>				<? endif ?>				<th>Действие</th>            </tr>            <? foreach ($products as $key => $product): ?>            <tr>                <td>                    <input type="hidden" name="product_id[<?= $key ?>]" value="<?= $product['id_product'] ?>">					<input type="hidden" name="product_code[<?= $key ?>]" value="<?= $product['code'] ?>">					<?= $product['title'] ?>, <span class="date"><?= $product['code'] ?></span>                </td>                <td>                    <select name="product_category[<?= $key ?>]">                        <option value=""></option>                        <? foreach ($all_categories as $cats): ?>                            <? if ($cats['code'] == $product['category']): ?>                            <option value="<?= $cats['code'] ?>" selected="selected"><?= $cats['title'] ?><? if ($cats['parent']): ?> (<?= $cats['parent'] ?>)<? endif; ?></option>                            <? else: ?>                            <option value="<?= $cats['code'] ?>"><?= $cats['title'] ?><? if ($cats['parent']): ?> (<?= $cats['parent'] ?>)<? endif; ?></option>                            <? endif; ?>                        <? endforeach; ?>                    </select>                </td>                <td><textarea name="product_description[<?= $key ?>]"><?= $product['description'] ?></textarea></td>				<? if ($config['store']['enabled'] === true): ?>					<td><input type="text" name="product_store[<?= $key ?>]" value="<?= $product['store'] ?>" class="input-mini"></td>				<? endif; ?>                <? if ($config['download']['enabled'] === true): ?>                <td>					<? if ($config['download']['link2files'] === true): ?>						<select name="product_link2file[<?= $key ?>]" class="input-medium">							<option value=""></option>							<? foreach($dir_files as $dir_file): ?>							<option value="<?= $dir_file ?>" <?= ($dir_file == $product['link2file']) ? 'selected="selected"' : ''?>><?= $dir_file ?></option>							<? endforeach; ?>						</select>					<? else: ?>						<? if (is_file(dirname(__FILE__) . '/../' .$config['download']['dir'] . '/' . $product['code'] . '.' . $config['download']['type'])): ?>						Текущий файл: <a href="<?= $config['sitelink'] . $config['dir'] ?>admin/products.php?download_file=<?= $product['code'] ?>.<?= $config['download']['type'] ?>">Скачать</a> <a href="<?= $config['sitelink'] . $config['dir'] ?>admin/products.php?delete_file=<?= $product['code'] ?>.<?= $config['download']['type'] ?>" title="Удалить файл" class="delete" onclick="return confirm('Вы действительно хотите удалить файл товара?')">Удалить</a><br>						<? else: ?>						Файл не загружен<br>						<? endif; ?>						<input type="file" name="product_file[<?= $key ?>]" accept="<?= $config['download']['mime'] ?>">					<?endif; ?>                </td>                <? endif ?>				<? if ($config['manager']['enabled'] === true): ?>				<td>                    <select name="product_manager[<?= $key ?>]">                        <option value=""></option>                        <? foreach ($managers as $manager): ?>                            <? if ($manager['id_manager'] == $product['manager']): ?>                            <option value="<?= $manager['id_manager'] ?>" selected="selected"><?= $manager['name'] ?></option>                            <? else: ?>                            <option value="<?= $manager['id_manager'] ?>"><?= $manager['name'] ?></option>                            <? endif; ?>                        <? endforeach; ?>                    </select>                </td>				<? endif ?>				<? if ($config['partner']['enabled'] === true && $config['partner']['rate_product'] === true): ?>                    <td><input type="text" name="product_partner_rate[<?= $key ?>]" value="<?= $product['partner_rate'] ?>" class="input-mini"></td>                <? endif ?>				<? if ($config['author']['enabled'] === true): ?>				 <td>                    <select name="product_author[<?= $key ?>]">                        <option value=""></option>                        <? foreach ($authors as $author): ?>                            <? if ($author['id_author'] == $product['author']): ?>                            <option value="<?= $author['id_author'] ?>" selected="selected"><?= $author['name'] ?></option>                            <? else: ?>                            <option value="<?= $author['id_author'] ?>"><?= $author['name'] ?></option>                            <? endif; ?>                        <? endforeach; ?>                    </select>                </td>				<? endif ?>				<? if ($config['reports']['cost_prices'] === true): ?>				<td>					<input type="text" name="product_cost_price[<?= $key ?>]" value="<?= $product['cost_price'] ?>" class="input-mini">				</td>				<? endif ?>                <td>					<a class="btn btn-success" title="Редактировать товар" href="<?= $config['sitelink'] . $config['dir'] ?>admin/codegen.php?id_product=<?= $product['id_product'] ?>"><i class="icon-eye-open icon-white"></i></a>					<a class="btn btn-danger" href="<?= $config['sitelink'] . $config['dir'] ?>admin/products.php?delete_product_id=<?= $product['id_product'] ?>" title="Удалить" onclick="return confirm('Вы действительно хотите удалить товар?')"><i class="icon-trash icon-white"></i></a>				</td>            </tr>            <? endforeach; ?>        </table>        <? endif; ?>        <?= (isset($pagination)) ? $pagination : '' ?>        <hr>        <button type="submit" name="products_edit" class="btn btn-success">Сохранить изменения</button>        <hr>        <h3>Добавить разделы:</h3>        <table class="table table-bordered table-hover table-striped table-condensed">            <tr>                <th>Код</th>                <th>Родитель</th>                <th>Наименование</th>            </tr>            <? for ($i = 0; $i <= 2; $i++): ?>            <tr>                <td><input type="text" name="new_category_code[<?= $i ?>]" size="20"></td>                <td>                    <select name="new_category_parent[<?= $i ?>]">                        <option value=""></option>                        <? foreach ($all_categories as $cats): ?>                            <? if ($cats['code'] == $current_category['code']): ?>                            <option selected="selected" value="<?= $cats['code'] ?>"><?= $cats['title'] ?></option>                            <? else: ?>                            <option value="<?= $cats['code'] ?>"><?= $cats['title'] ?></option>                            <? endif; ?>                        <? endforeach; ?>                    </select>                </td>                <td><input type="text" name="new_category_title[<?= $i ?>]" size="40"></td>            </tr>            <? endfor; ?>        </table>				<button type="submit" name="products_edit" class="btn btn-success">Сохранить изменения</button>		или 		<a href="<?= $config['sitelink'] . $config['dir'] ?>admin/codegen.php" class="btn btn-primary">Создать новый товар</a>    </form></body></html>