bled'] === true): ?>				<td><?= number_format($order['author_sum'], 2, '.', '') ?></td>			<? endif; ?>			<? if ($config['manager']['enabled'] === true): ?>				<td><?= number_format($order['manager_sum'], 2, '.', '') ?></td>			<? endif; ?>			<td><?= number_format($order['real_profit'], 2, '.', '') ?></td>        </tr>		<? $sum = $order['sum'] - $order['delivery_cost'] ?>		<?			$col_quantity += $order['quantity'];			$col_sum += $sum;			$col_delivery_cost += $order['delivery_cost'];			$col_product_cost += (isset($order['product_cost'])) ? $order['product_cost'] : 0;			$col_profit += $order['profit'];			$col_ads += $order['ads'];			$col_partner += $order['partner_sum'];			$col_partner2 += $order['partner2_sum'];			$col_author += $order['author_sum'];			$col_manager += $order['manager_sum'];			$col_real_profit += $order['real_profit'];		?>        <? endforeach ?>		<tr>			<th colspan="3"></th>			<th><?= $col_quantity ?></th>			<th><?= number_format($col_sum, 2, '.', '') ?></th>			<th><?= number_format($col_delivery_cost, 2, '.', '') ?></th>			<th><?= number_format($col_product_cost, 2, '.', '') ?></th>			<? if ($config['yandex']['enabled'] === true): ?>				<th><?= number_format($col_profit, 2, '.', '') ?></th>				<th><?= number_format($col_ads, 2, '.', '') ?></th>			<? endif; ?>			<? if ($config['partner']['enabled'] === true): ?>				<th><?= number_format($col_partner, 2, '.', '') ?></th>			<? endif; ?>			<? if ($config['partner']['enabled'] === true && $config['partner']['levels'] === 2): ?>				<th><?= number_format($col_partner2, 2, '.', '') ?></th>			<? endif; ?>			<? if ($config['author']['enabled'] === true): ?>				<th><?= number_format($col_author, 2, '.', '') ?></th>			<? endif; ?>			<? if ($config['manager']['enabled'] === true): ?>				<th><?= number_format($col_manager, 2, '.', '') ?></th>			<? endif; ?>			<th><?= number_format($col_real_profit, 2, '.', '') ?></th>		</tr>    </table>	<? if ($_SESSION['access_type'] 