<?/*Шаблон списка заказов в админке===============================*/?><?# Подключение шапки админкиinclude_once dirname(__FILE__) . '/../design/adminHeader.tpl.php';?><br><div class="container-fluid">	<ul class="breadcrumb">		<div class="control-group">			<div class="controls">				<form method="post" class="form-search form-horizontal no_margin">					дата 					с: 	<input type="text" name="date_from" class="datepicker input-small" value="<?=(isset($date_from_form)) ? date("d.m.Y", strtotime($date_from_form)) : '' ?>" />										по: <input type="text" name="date_to" class="datepicker input-small" value="<?=(isset($date_to_form)) ? date("d.m.Y", strtotime($date_to_form)) : '' ?>" />										<button class="btn btn-primary" name="search_submit">Фильтровать</button>										&nbsp;&nbsp;&nbsp;					<input type="checkbox" name="refunds" value="count" id="refunds" <?if(isset($_POST['refunds'])):?>checked="checked"<?endif;?>>					<label for="refunds">Учитывать возвраты</label>					<?if(isset($_POST['refunds'])):?><input type="hidden" name="was_refunds" value="true"><?endif;?>				</form>			</div>		</div>	</ul>	<? if (isset($error_alert)): ?>	<div class="alert alert-error">		<button type="button" class="close" data-dismiss="alert">&times;</button>		<?= $error_alert ?>	</div>	<? endif; ?>	<form method="post">    <table class="table table-striped table-bordered table-condensed data-table table-hover">        <tr>			<th>Дата</th>			<? if ($config['yandex']['enabled'] === true): ?>				<th>Клики</th>				<th>Расход, <?= $config['currency'] ?></th>				<th>Ср.цена, <?= $config['currency'] ?></th>			<? endif; ?>            <th>Кол-во заказов</th>			<? if ($config['yandex']['enabled'] === true): ?>				<th>Цена 1 заказа, <?= $config['currency'] ?></th>			<? endif; ?>			<? if ($_SESSION['access_type'] == 'admin'): ?>			<th>Оборот<br>за день, <?= $config['currency'] ?></th>			<th>Доставка, <?= $config['currency'] ?></th>			<th>Себестоимость,<br><?= $config['currency'] ?></th>			<? if ($config['partner']['enabled'] === true): ?>				<th>Партнёру,<br><?= $config['currency'] ?></th>			<? endif; ?>			<? if ($config['partner']['enabled'] === true && $config['partner']['levels'] === 2): ?>				<th>Партнёру<br>2 ур., <?= $config['currency'] ?></th>			<? endif; ?>			<? if ($config['author']['enabled'] === true): ?>				<th>Автору,<br><?= $config['currency'] ?></th>			<? endif; ?>			<? if ($config['manager']['enabled'] === true): ?>				<th>Менеджеру,<br><?= $config['currency'] ?></th>			<? endif; ?>            <th>Чистая прибыль,<br><?= $config['currency'] ?></th>			<? endif; ?>        </tr>		<? $col_clicks = $col_rate = $col_price = $col_orders = $col_order_price = $col_real_profit = $col_turnover = $col_sum = $col_partner = $col_partner2 = $col_author = $col_manager = $col_profit_without_ads = $col_product_cost = $col_delivery_cost = 0; ?>        <? foreach ($days as $key => $day): ?>			<tr>				<td><?= date("d.m.Y", strtotime($day['date'])) ?></td>				<? if ($config['yandex']['enabled'] === true): ?>					<td><?= $day['clicks'] ?></td>					<td><?= number_format($day['rate'], 2, '.', '') ?></td>					<td><?= number_format($day['price'], 2, '.', '') ?></td>				<? endif; ?>				<td><?= $day['orders'] ?></td>				<? if ($config['yandex']['enabled'] === true): ?>					<td><?= number_format($day['order_price'], 2, '.', '') ?></td>				<? endif; ?>				<? if ($_SESSION['access_type'] == 'admin'): ?>				<td><?= number_format($day['turnover'], 2, '.', '') ?></td>				<td><?= number_format($day['delivery_cost'], 2, '.', '') ?></td>				<td><?= number_format($day['product_cost'], 2, '.', '') ?></td>				<? if ($config['partner']['enabled'] === true): ?>					<td><?= number_format($day['partner_sum'], 2, '.', '') ?></td>				<? endif; ?>				<? if ($config['partner']['enabled'] === true && $config['partner']['levels'] === 2): ?>					<td><?= number_format($day['partner2_sum'], 2, '.', '') ?></td>				<? endif; ?>				<? if ($config['author']['enabled'] === true): ?>					<td><?= number_format($day['author_sum'], 2, '.', '') ?></td>				<? endif; ?>				<? if ($config['manager']['enabled'] === true): ?>					<td><?= number_format($day['manager_sum'], 2, '.', '') ?></td>				<? endif; ?>				<td><?= number_format($day['real_profit'], 2, '.', '') ?></td>				<? endif; ?>			</tr>			<?				$col_clicks += $day['clicks'];				$col_rate += $day['rate'];				$col_price += $day['price'];				$col_orders += $day['orders'];				$col_product_cost += $day['product_cost'];				$col_order_price += $day['order_price'];				$col_turnover += $day['turnover'];				$col_delivery_cost += $day['delivery_cost'];				$col_sum += $day['sum'];				$col_partner += $day['partner_sum'];				$col_partner2 += $day['partner2_sum'];				$col_author += $day['author_sum'];				$col_manager += $day['manager_sum'];				$col_real_profit += $day['real_profit'];				$col_profit_without_ads += $day['profit_without_ads'];			?>        <? endforeach ?>		<tr>			<th></th>			<? if ($config['yandex']['enabled'] === true): ?>				<th><?= $col_clicks ?></th>				<th><?= number_format($col_rate, 2, '.', '') ?></th>				<th><?= number_format($col_price / count($days), 2, '.', '') ?></th>			<? endif; ?>			<th><?= $col_orders ?></th>			<? if ($config['yandex']['enabled'] === true): ?>				<th><?= number_format($col_order_price / count($days), 2, '.', '') ?></th>			<? endif; ?>			<? if ($_SESSION['access_type'] == 'admin'): ?>				<th><?= number_format($col_turnover, 2, '.', '') ?></th>				<th><?= number_format($col_delivery_cost, 2, '.', '') ?></th>				<th><?= number_format($col_product_cost, 2, '.', '') ?></th>				<? if ($config['partner']['enabled'] === true): ?>					<th><?= number_format($col_partner, 2, '.', '') ?></th>				<? endif; ?>				<? if ($config['partner']['enabled'] === true && $config['partner']['levels'] === 2): ?>					<th><?= number_format($col_partner2, 2, '.', '') ?></th>				<? endif; ?>				<? if ($config['author']['enabled'] === true): ?>					<th><?= number_format($col_author, 2, '.', '') ?></th>				<? endif; ?>				<? if ($config['manager']['enabled'] === true): ?>					<th><?= number_format($col_manager, 2, '.', '') ?></th>				<? endif; ?>			<th><?= number_format($col_real_profit, 2, '.', '') ?></th>			<? endif; ?>		</tr>    </table>	<div class="parameters">	<h3>Сумма всех заказов на странице: <?= number_format($col_sum, 2, '.', '') ?> <?= $config['currency'] ?> (с учётом доставки)</h3>	<h4 title="Кроме удалённых" class="tip"><span class="width_260">Кол-во заказов всего:</span><span class="width_150 t_right"><?= (isset($all_orders)) ? $all_orders : '0' ?></span></h4>	<h4><span class="width_260">Кол-во заказов на странице:</span><span class="width_150 t_right"><?= $orders_count ?></span></h4>		<? if ($config['yandex']['enabled'] === true): ?>	<h4><span class="width_260">1 заказ:</span><span class="width_150 t_right"><?= ($col_orders != 0) ? number_format($col_clicks / $col_orders, 0, '.', '') : 0 ?> кликов</h4>	<h4><span class="width_260">1 заказ:</span><span class="width_150 t_right"><?= ($col_orders != 0) ? number_format($col_rate / $col_orders, 2, '.', '') : 0 ?> <?= $config['currency'] ?></h4>	<h4><span class="width_260">ROI:</span><span class="width_150 t_right"><?= ($col_rate != 0) ? number_format($col_profit_without_ads / $col_rate * 100, 2, '.', '') : 0 ?>%</h4>	<h4><span class="width_260">Конверсия:</span><span class="width_150 t_right"><?= ($col_clicks != 0) ? number_format($col_orders / $col_clicks * 100, 2, '.', '') : 0 ?>%</h4>	<? if (isset($refund_count)): ?>	<h4><span class="width_260">Возвратов на странице:</span><span class="width_150 t_right"><?= $refund_count ?> (<?= $refund_percent ?>%)</span></h4>	<? endif; ?>	<? endif; ?>	</div>		<br>	<div class="form-actions">		<div class="pull-right input-append">			<label>Последних дней на странице:</label>			<select name="reportList" class="input-mini" id="order_list">				<? if ($_SERVER['REQUEST_METHOD'] == 'POST'): ?><? $reportList = 0; ?><option value="-"> </option><? endif; ?>				<option value="7" <?=(isset($reportList) && $reportList == 7)? 'selected="selected"': ''?>>7</option>				<option value="14" <?=(isset($reportList) && $reportList == 14)? 'selected="selected"': ''?>>14</option>				<option value="21" <?=(isset($reportList) && $reportList == 21)? 'selected="selected"': ''?>>21</option>				<option value="28" <?=(isset($reportList) && $reportList == 28)? 'selected="selected"': ''?>>28</option>			</select>		</div>	</div>	</form>	</div><script type="text/javascript">	$('#order_list').change(function(){		$(this).parent().parent().parent().submit();	});		$('#refunds').change(function(){		$(this).parent().submit();	});	$('.datepicker').datepicker({		'language' : 'ru',		'autoclose' : true,		'weekStart' : 1,		'format' : 'dd.mm.yyyy'	});		$('.tip').tooltip();</script></body></html>