<?/*Шаблон индекса админки======================*/?><!DOCTYPE html><html xmlns="http://www.w3.org/1999/xhtml"><?# Подключение шапки админкиinclude_once dirname(__FILE__) . '/../design/adminHeader.tpl.php';?>	<br/>    <div class="container-fluid">		<? if (isset($message)): ?>		<div class="alert alert-error fade in">			<button type="button" class="close" data-dismiss="alert">&times;</button>			<?= $message ?>		</div>		<? endif ?>				<p class="text-info">Здесь находится статистика вашей работы.</p>				<? if ($config['partner']['links']['enabled'] === true): ?>		<div class="well">		<h4>Привлечение клиентов</h4>		<p>Переходов по ссылке: <?= $partner['clicks'] ?></p>		<p>Заказов оформлено: <?= $total_count ?></p>		<p>Заказов оплачено: <?= $paid_count ?></p>		<hr/>		<p>Вами заработано: <?= number_format($paid_sum, 2, '.', '') ?> <?= $config['currency'] ?></p>		</div>				<h5 class="orders_list btn">Список заказов</h5>		<table id="orders_list" class="table table-striped table-bordered table-hover table-condensed">		<tr>			<th>ID</th>			<th>Дата</th>			<th>Статус</th>			<th>Товары</th>			<th>Сумма</th>			<th>Ваша комиссия</th>		</tr>		<? foreach ($orders as $order): ?>		<tr>			<td><?= $order['id_custom'] ?></td>			<td><?= $order['date'] ?></td>			<td <?= (in_array($order['status'], $success_statuses)) ? 'class="text-success"' : '' ?>><?= $config['statuses'][$order['status']] ?></td>			<td><? foreach ($order['items'] as $item): ?><?= $item['product'] ?> - <?= $item['quantity'] ?> <?= $item['unit'] ?> по <?= $item['price'] ?> <?= $config['currency'] ?><br><? endforeach; ?></td>			<td><?= $order['sum'] ?> <?= $config['currency'] ?></td>			<td><?= (isset($order['commission'])) ? $order['commission'] : 0 ?> <?= $config['currency'] ?></td>		</tr>		<? endforeach; ?>		</table>		<? endif; ?>		<? if ($config['partner']['levels'] == 2): ?>		<br/><br/>		<div class="well">		<h4>Привлечение партнёров</h4>		<p>Вами привлечено: <?= $referers_count ?> чел.</p>		<p>Ими заработано: <?= number_format($referers_sum, 2, '.', '') ?> <?= $config['currency'] ?></p>		<hr/>		<p>Ваш процент: <?= number_format($referers_profit, 2, '.', '') ?> <?= $config['currency'] ?></p>		</div>				<h5 class="referers_list btn">Список партнёров</h5>		<table id="referers_list" class="table table-striped table-bordered table-hover table-condensed">		<tr>			<th>Email</th>			<th>Выплачено</th>		</tr>		<? foreach ($referers as $referer): ?>		<tr>			<td><?= $referer['email'] ?></td>			<td><?= $referer['paid'] ?> <?= $config['currency'] ?></td>		</tr>		<? endforeach; ?>		</table>		<? endif; ?>				<p><br/>Вам выплачено: <?= $partner['paid'] ?> <?= $config['currency'] ?></p>		<h4 <? if ($paid_sum + $referers_sum - $partner['paid'] != 0): ?>class="text-success"<? endif ?>>Вам осталось выплатить: <?= number_format($paid_sum + $referers_profit - $partner['paid'], 2, '.', '') ?> <?= $config['currency'] ?></h4>    </div><script type="text/javascript">	$(document).ready(function() {			$(".orders_list").click(function () {			$("#orders_list").toggle('fast');		});			$(".referers_list").click(function () {			$("#referers_list").toggle('fast');		});	});</script></body></html>